// import React from 'react';
import React, { useState } from 'react';
import { View, Text, TouchableOpacity,Modal } from 'react-native';

const CryptoAssets = () => {
    const [modalVisible, setModalVisible] = useState(false);
  
    const handleSeeAll = () => {
      setModalVisible(true);
    };
  
    const closeModal = () => {
      setModalVisible(false);
    };

  return (
    <View style={styles.containerAssets}>
      
      <Text style={styles.assetText}>Crypto Assets</Text>
      <TouchableOpacity style={styles.button} onPress={handleSeeAll}>
        <Text style={styles.buttonText}>See All</Text>
      </TouchableOpacity>

      <Modal visible={modalVisible} animationType="slide" onRequestClose={closeModal}>
        <View style={styles.modalContainer}>
          <Text style={styles.modalText}>Crypto Asset List</Text>
          <TouchableOpacity style={styles.modalCloseButton} onPress={closeModal}>
            <Text style={styles.modalCloseButtonText}>Close</Text>
          </TouchableOpacity>
        </View>
      </Modal>



      
    </View>
  );
};

const styles = {
//   containerAssets: {
//     flexDirection: 'row',
//     alignItems: 'center',
//     justifyContent: 'space-between',
//     paddingHorizontal: 16,
//     paddingVertical: 8,
//    backgroundColor: '#f2f2f2',
//   },

containerAssets: {
    marginTop:20,
    flexDirection: 'row',
    alignItems: 'flex-start',
    justifyContent: 'space-between',
    paddingHorizontal: 16,
    paddingVertical: 16, // Increase the vertical padding to increase the height
    backgroundColor: '#f2f2f2',
    //height:500,
    borderRadius:30
  },
  button: {
    backgroundColor: 'black',
    paddingVertical: 8,
    paddingHorizontal: 12,
    borderRadius: 15,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
  },
  assetText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
};

export default CryptoAssets;
