// Header.js
import React,{useState} from 'react';
import { View, Text, Image, TouchableOpacity,TextInput } from 'react-native';

const Header = () => {
    const [isSearchVisible, setSearchVisible] = useState(false);


    const toggleSearch = () => {
        console.log(isSearchVisible);
      setSearchVisible(!isSearchVisible);
    };
  return (
    <>
    <View style={styles.nav}>
      <View style={styles.leftContent}>
        <Image
          style={styles.logo}
          source={require("../assets/mask-group.png")}
        />
        <Text style={styles.welcomeText}>Welcome </Text>
        <Text style={styles.userName}>Satyam</Text>
      </View>
      <TouchableOpacity onPress={toggleSearch}>
        <Image
          style={styles.rightImage1}
          source={require("../assets/mask-group2.svg")} // Change to your search icon image source
        />
      </TouchableOpacity>
      <Image
        style={styles.rightImage}
        source={require("../assets/mask-group1.svg")}
      />
      
      
    </View>
     {isSearchVisible ? (
        <TextInput
        style={styles.searchBox}
        placeholder="Search"
        onChangeText={(text) => {
          console.log(text);
        }}
      />
       ) : (<></>
 
       )}
       </>
  );
};



const styles = {
  
  nav: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    borderBottomWidth: 1,
    borderColor: '#ccc',
    paddingVertical: 10,
  },
  leftContent: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logo: {
    width: 40,
    height: 40,
    marginRight: 10,
  },
  welcomeText: {
    fontSize: 18,
  },
  userName: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  rightImage: {
    width: 40,
    height: 40,
    marginLeft: 10,
  },
  rightImage1: {
    width: 40,
    height: 40,
    marginLeft: 90,
  },
  searchBox: {
    marginTop: 16,
    paddingHorizontal: 16,
    paddingVertical:10,
    height: 40,
    borderWidth: 1,
    borderColor: 'lightgray',
    borderRadius: 8,
    marginHorizontal: 16,
    backgroundColor:'red',
  },
};

export default Header;
